import React, { useState } from "react";

import styles from "./Gallery.module.css";

import Modal from '../UI/Modal';

let listOfImages = [];

const Gallery = () => {
    const [isShowingPicture, setIsShowingPicture] = useState(false);

  const importAll = (r) => {
    return r.keys().map(r);
  };

  listOfImages = importAll(
    require.context("../../Images/Yoga/", false, /\.(png|jpe?g|svg)$/)
  );

  const showPictureHandler = (event) => {
      setIsShowingPicture(true);
  }

  const hidePictureHandler = (event) => {
      setIsShowingPicture(false);
  }

  const modalImage = (src, alt) => {
      <img src={src} alt={alt} />
  }

  return (
    <>
      <header>
        <h1>Photo Gallery coming soon!</h1>
      </header>
      <div className={styles.gallery}>
        {listOfImages.map((image, index) => (
          <React.Fragment key={index}><img
            onClick={showPictureHandler}

            src={image.default}
            alt="Rachel doing yoga"
            className={styles.picture}
          />
              <Modal message={modalImage(image.default, 'Rachel doing yoga')} onConfirm={hidePictureHandler} />
          </React.Fragment>
        ))}
      </div>
    </>
  );
};

export default Gallery;
