import React, { useEffect, useState, useCallback } from "react";
import AffirmationDeckCard from "./AffirmationCard";
import AffirmationDeckForm from "./AffirmationDeckForm/AffirmationDeckForm";

const AffirmationDeck = () => {
  const [deck, setDeck] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [display, setDisplay] = useState('form');
  const [selectedCard, setSelectedCard] = useState({});

  const fetchDeck = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        "https://rachel-clifton-yoga-default-rtdb.firebaseio.com/affirmation-deck.json"
      );
      if (!response.ok) {
        throw new Error(
          "Sorry! There was an issue accessing the Virtual Affirmation Deck."
        );
      }
      const data = await response.json();

      const transformedDeck = [];
      for (const key in data) {
        transformedDeck.push({
          id: key,
          affirmation: data[key].affirmation,
          mantra: data[key].mantra,
          category: data[key].category,
        });
      }
      setDeck(transformedDeck);
    } catch (newError) {
      setError(newError.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => fetchDeck(), [fetchDeck]);

  const onDraw ={
    setDisplay,
    setSelectedCard
  }

  let displayContent;
  if (display==='form'){
    displayContent = <AffirmationDeckForm onDraw={onDraw} deck={deck}/>
  }
  if (display==='card'){
    displayContent = <><AffirmationDeckCard card={selectedCard}/></>
  }

  let content;

  if(deck.length > 0) {
    console.log('deck')
    content = displayContent;
  }
  if (error) {
    console.log(error)
    content = <p>{error}</p>;
  }
  if (isLoading) {
    console.log('loading')
    content = <p>Loading...</p>;
  }

  return (
    <>
      <h1>Virtual Affirmation Deck</h1>
      {content}
    </>
  );
};

export default AffirmationDeck;
