  return (
    <div>
      <h2>Enjoyed the music during class?</h2>
      <h3>Check out my Spotify playlists:</h3>
      <button value="namaste" onClick={playerHandler}>
        Namaste Playlist (Aerial & Roots)
      </button>
      <button value="shanti" onClick={playerHandler}>
        Shanti Playlist (Gentle & Yin)
      </button>
      <button value="stress" onClick={playerHandler}>
