import React, { useState } from "react";

import Card from "../UI/Card";

const Music = () => {
  const [player, setPlayer] = useState("favorites");

  const namaste = (
    <iframe
      src="https://open.spotify.com/embed/playlist/5GUWfzFVXVoU0HlLNrjelo"
      width="300"
      height="380"
      frameBorder="0"
      allowtransparency="true"
      allow="encrypted-media"
      title="namaste"
    ></iframe>
  );

//   const favorites = (
//     <iframe
//       src="https://open.spotify.com/embed/playlist/7zz0hXaK9mMS3kJsvcKFTH"
//       width="300"
//       height="380"
//       frameborder="0"
//       allowtransparency="true"
//       allow="encrypted-media"
//     ></iframe>
//   );

  const shanti = (
    <iframe
      src="https://open.spotify.com/embed/playlist/5lyCYRUo96NuP85J6DH2k1"
      width="300"
      height="380"
      frameborder="0"
      allowtransparency="true"
      allow="encrypted-media"
    ></iframe>
  );

  const stress = (
    <iframe
      src="https://open.spotify.com/embed/playlist/3LRoizMcjJhtFHoWQdESPb"
      width="300"
      height="380"
      frameborder="0"
      allowtransparency="true"
      allow="encrypted-media"
    ></iframe>
  );

  const playerHandler = (event) => {
    setPlayer("namaste");
    console.log(player);
  };

  return (
    <div>
      <h2>Enjoyed the music during class?</h2>
      <h3>Check out my Spotify playlists:</h3>
      <p onClick={playerHandler}>Namaste Playlist (Aerial & Roots)</p>
      <p>Shanti Playlist (Gentle & Yin)</p>
      <p>Yoga for Stress Playlist</p>
      <p>Rachel's Favorites</p>

      <Card>
        <p>{player}</p>
        <div>{player === "namaste" && { namaste }}</div>
        <div>{player === "shanti" && { shanti }}</div>
        <div>{player === "stress" && { stress }}</div>
        <div>{player === "favorites" &&     <iframe
      src="https://open.spotify.com/embed/playlist/7zz0hXaK9mMS3kJsvcKFTH"
      width="300"
      height="380"
      frameborder="0"
      allowtransparency="true"
      allow="encrypted-media"
    ></iframe>}</div>
      </Card>
    </div>
  );
};

export default Music;
